fn desAssert(des_tests: []const DesTest) !void {
    var des = Des.Des.init();
    for (des_tests) |t| {
        var keyBuf: [8]u8 = undefined;
        const key = try fmt.hexToBytes(&keyBuf, t.key);
        var ivBuf: [8]u8 = undefined;
        var iv: ?[]const u8 = null;
        if (t.iv) |value| {
            iv = try fmt.hexToBytes(&ivBuf, value);
        }
        var tBuf: [1024]u8 = undefined;
        const text = try fmt.hexToBytes(&tBuf, t.text);
        var cBuf: [1024]u8 = undefined;
        const cipher = try fmt.hexToBytes(&cBuf, t.ciph);

        des.encrypt(key, iv, text);
        if (t.result == .valid) {
            debug.assert(mem.eql(u8, des.cipher[0..cipher.len], cipher));
        } else {
            debug.assert(!mem.eql(u8, des.cipher[0..cipher.len], cipher));
        }

        des.decrypt(key, iv, cipher);
        if (t.result == .valid) {
            debug.assert(mem.eql(u8, des.cipher[0..text.len], text));
        } else {
            debug.assert(!mem.eql(u8, des.cipher[0..text.len], text));
        }
    }
}

fn tripleDesAssert(triple_des_tests: []const TripleDesTest) !void {
    var triple_des = Des.TripleDes.init();
    for (triple_des_tests) |t| {
        var key = Des.TripleDesKey.initFromHex(t.key);
        var ivBuf: [8]u8 = undefined;
        var iv: ?[]const u8 = null;
        if (t.iv) |value| {
            iv = try fmt.hexToBytes(&ivBuf, value);
        }
        var tBuf: [1024]u8 = undefined;
        const text = try fmt.hexToBytes(&tBuf, t.text);
        var cBuf: [1024]u8 = undefined;
        const cipher = try fmt.hexToBytes(&cBuf, t.ciph);

        triple_des.encrypt(key, iv, text);
        if (t.result == .valid) {
            debug.assert(mem.eql(u8, triple_des.cipher[2][0..cipher.len], cipher));
        } else {
            debug.assert(!mem.eql(u8, triple_des.cipher[2][0..cipher.len], cipher));
        }

        triple_des.decrypt(key, iv, cipher);
        if (t.result == .valid) {
            debug.assert(mem.eql(u8, triple_des.cipher[2][0..text.len], text));
        } else {
            debug.assert(!mem.eql(u8, triple_des.cipher[2][0..text.len], text));
        }
    }
